import React, { useState, useRef, useCallback } from 'react';
import Note from './Note';
import { useDrop } from 'react-dnd';
import { notesList, columnsList } from '../data/panel-data';
import Column from './Column';



function DragDrop() {

    const [cards, setCards] = useState(notesList); // declare the final destination/notes here
    const [columns, setColumns] = useState(columnsList);

    const moveImage = (dragIndex, hoverIndex) => {
        setCards((prevcards) => {
            const newItems = prevcards.filter((_, idx) => idx !== dragIndex);
            newItems.splice(hoverIndex, 0, cards[dragIndex]);
            console.log("moveImage: " + dragIndex, hoverIndex, newItems);
            return [...newItems];
        });
    };

    const addImageTocards = (id) => {
        console.log("Dragging is over"); // printed when isOver is true
        const pictureList = notesList.filter((picture) => id === picture.id); // find the dragged image
        // notesList = notesList.filter((picture) => id !== picture.id);
        setCards((cards) => [...cards, pictureList[0]]); // add to the cards
    }

    return (
        <>
            {/* <div className='Pictures' > 
    {PicturesList.map((picture) => {
        return <Picture key={picture.id} id={picture.id} url={picture.url}/>;
    })} </div> */}
            {/* ref={drop} */}
            <div className='cards'>
                {/*  onDrop={onDrop} */}
                {/* , color: isOver  */}
                {/* {console.log(cards)} */}
                {columns.map((column) => {
                    return (
                        <div key={column.columnName} className={"column-wrapper"}>
                            <h3 className='column-title'>{column.columnName.toUpperCase()}</h3>
                            <Column>
                                {cards.filter((item,index)=>column.columnName === item.columnName).map((item, index) => {
                                    return <Note
                                    key={item.id}
                                    item={item}
                                    index={index}
                                    moveImage={moveImage}
                                />;
                                })}
                            </Column>
                        </div>
                    )
                })}
                {/* {cards.map((picture, index) => {
                    return <Note
                        key={picture.id}
                        id={picture.id}
                        url={picture.url}
                        index={index}
                        moveImage={moveImage}
                    // ref={ref}
                    />;
                })} */}
            </div>
        </>
    )
}

export default DragDrop;
