import { useDrop } from "react-dnd";

import classes from './Column.module.css';

const Column = ({ onDrop, columnName, children}) => {
    const [{isOver}, drop] = useDrop({
		accept: "card",
		collect(monitor) {
			return {
				isOver: monitor.isOver()
			}
		},
		drop(item, monitor) {
            onDrop(item, columnName);
        }
	})
    const extraClass = isOver ? " highlighted" : "";

    return (
        //  ref={drop}
        <div ref={drop} className={`${classes.column} ${extraClass}`}>
             {children}
        </div>

    )
}

export default Column;
