import React from 'react';
import { Form, json, Link, redirect, useActionData, useSearchParams } from 'react-router-dom';

export const authAction = async ({ params, request }) => {

  let searchParams = new URL(request.url).searchParams;
  let isLogin = searchParams.get('mode') === 'login';
  let mode = searchParams.get('mode') === 'login' ? 'login' : 'signup';
  console.log("isLogin: ");

  let formData = await request.formData();
  const data = {
    email: formData.get('email'),
    password: formData.get('password'),
  };
  console.log(data);
  console.log("isLogin: ", isLogin, formData);

  const errors = {};

  // validate the fields
  if (typeof data.email !== "string" || !data.email.includes("@")) {
    errors.email =
      "That doesn't look like an email address";
  }

  if (typeof data.password !== "string" || data.password.length < 6) {
    errors.password = "Password must be > 6 characters";
  }
  console.log(errors);

  if (Object.keys(errors).length) {
    // return [errors, redirect("/auth?mode=" + mode)];
    return errors;

  }
  console.log('http://localhost:8080/' + mode);
  /*let result;
      try {
        // const response = await fetch('https://website');
        result = await fetch('http://localhost:8080/' + mode, {
          method: "POST",
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(data),
        })
      } catch (error) {
        // TypeError: Failed to fetch
        console.log('There was an error', error);
      }*/
  /*let response;

  try {
    response = await fetch('http://localhost:8080/' + mode, {
      method: "POST",
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data),
    })
  } catch (error) {
    console.log('There was an error', error);
  }

  // Uses the 'optional chaining' operator
  if (response?.ok) {
    console.log('Use the response here!');
  } else {
    if (response.status === 422 || response.status === 401) {
      // return response;
      console.log("response.serverErrors", response.serverErrors);
      return [response.serverErrors, redirect("/auth?mode=" + mode)];
    }
    if (!response.ok) {
      throw json({ message: 'Could not authenticate user.' }, { status: 500 })
    }
    return redirect("/auth?mode=" + mode);
    console.log(`HTTP Response Code: ${response?.status}`)
  }*/
  let response;
  try {
    response = await fetch('http://localhost:8080/' + mode, {
      method: "POST",
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data),
    })

  } catch (error) {

  }
  const resData = await response.json();


  if (response.status === 422 || response.status === 401) {
    // return response;
    // return redirect("/post");

    // return response.serverErrors;
    // let serverErrors = await response.json();
    console.log("response.serverErrors", resData.serverErrors);
    return resData.serverErrors; //, redirect("/auth?mode=" + mode),  serverErrors NOT SHOWN

  }
  // if (!response.ok) {
  //   throw json({ message: 'Could not authenticate user.' }, { status: 500 })
  // }

  const token = resData.token;
  localStorage.setItem('token', token);
  const expiration = new Date().getTime() + 60 * 60 * 1000;
  // console.log(new Date().getTime(), expiration, new Date(expiration).toISOString());
  localStorage.setItem('expiration-token', new Date(expiration).toISOString());


  // // otherwise create the user and redirect
  // await createUser(email, password);
  return redirect("/");

  // return formData; /// HOW to redirect & pass input data??
}

const AuthPage = () => {
  let errors = useActionData();
  // if (Array.isArray(errors)) {
  //   errors = errors[0];
  // }
  // let searchParams = new URL(request.url).searchParams;
  // let searchParams = new URLSearchParams(window.location.pathname);
  // console.log(searchParams.get('mode'));
  // let isLogin = searchParams.get('mode') === 'login';

  const [searchParams] = useSearchParams();
  let isLogin = searchParams.mode === 'login';

  // let mode = searchParams.get('mode') === 'login' ? 'login' : 'signup';
  return (
    <>
      <Form method="post">
        <label htmlFor='email'>Email</label>
        <input id="email" name="email"></input>
        {errors?.email && <div>{errors.email}</div>}
        {/* {errors[0]?.email && <div>{errors[0].email}</div>} */}
        {/* {serverErrors?.email && <div>{serverErrors.email}</div>} */}
        <label htmlFor='password'>Password</label>

        <input id="password" name="password"></input>
        {errors?.password && <div>{errors.password}</div>}
        {!isLogin &&
          <>
            <label htmlFor='name'>Name</label>
            <input id="name" name="name"></input>
          </>
        }
        <button>{isLogin ? 'Login' : 'Sign up'}</button>
      </Form>
    </>
  )
}

export default AuthPage;
