import React from "react";
import { useDrop } from "react-dnd";
import ITEM_TYPE from "../data/types";
import { statuses } from "../data";


// each outer column wrapper
const DropWrapper = ({ onDrop, children, status }) => {
    const [{ isOver }, drop] = useDrop({
        accept: ITEM_TYPE,
        // canDrop: (item, monitor) => {
        //     const itemIndex = statuses.findIndex(si => si.status === item.status);
        //     const statusIndex = statuses.findIndex(si => si.status === status);
        //     console.log(item.status, status);
        //     return [itemIndex + 1, itemIndex - 1, itemIndex].includes(statusIndex);// check if the start & targeted column are next to each other
        // },
        drop: (item, monitor) => {
            console.log(item);
            onDrop(item, monitor, status);
        },
        collect: monitor => ({
            isOver: monitor.isOver()
        })
    });

    return (
        <div ref={drop} className={`drop-wrapper${isOver ? ' highlight-region' : ''}`}>
            {React.cloneElement(children, { isOver })}
        </div>
    )
};

export default DropWrapper;
