            // },
            {
                path: '/signin',
                element: <div>Signin</div>
            }
        ]
    },{
        path: 'signup',
        element: <Header/>
    },
])
const App = () => {
    // {/* <Header />
    // <AuthContextProvider>
    //     <DndProvider backend={HTML5Backend}>
    //         <Homepage />
    //     </DndProvider>
    // </AuthContextProvider> */}

    return (
        // <React.StrictMode>

        <RouterProvider router={router}/>
        // </React.StrictMode>

        // </RouterProvider>
    );
};

export default App;
