import React, { useState, useRef } from "react";
import ReactDOM from "react-dom";
import { useDrag, useDrop } from "react-dnd";
import Modal from './Modal';

import classes from './Task.module.css';

// hovered component
const Task = ({ task, index, moveItem, status, deleteTask }) => {
    const ref = useRef(null);

    // dragged component
    /*const [, drop] = useDrop({
        accept: "task",
        drop(item, monitor) {
            console.log("FROP");
            let { statusId, icon, statusTitle, ...draggedtask } = item; // TO DO destruct somehow status & task
            if (!ref.current) {
                return
            }
            // console.log(statusId, status.statusId);
            // console.log(statusId1, icon, task);
            const dragIndex = draggedtask.index;
            const hoverIndex = index;
            console.log(dragIndex, hoverIndex);
            const hoverStatusId = status.statusId;

            if (dragIndex === hoverIndex) {
                return
            }

            // Determine rectangle on screen
            // const hoverBoundingRect = ref.current?.getBoundingClientRect()
            // // Get element vertical middle
            // const hoverMiddleY =
            //     (hoverBoundingRect.bottom - hoverBoundingRect.top) / 2
            // // Determine mouse position
            // const clientOffset = monitor.getClientOffset()
            // // Get pixels to the top of element
            // const hoverClientY = clientOffset.y - hoverBoundingRect.top
            // // Only perform the move when the mouse has crossed half of the items height
            // // When dragging downwards, only move when the cursor is below 50%
            // // When dragging upwards, only move when the cursor is above 50%
            // console.log(hoverBoundingRect, hoverMiddleY, clientOffset, hoverClientY);
            // // Dragging downwards
            // if (dragIndex < hoverIndex && hoverClientY < hoverMiddleY) {
            //     return
            // }
            // // Dragging upwards
            // if (dragIndex > hoverIndex && hoverClientY > hoverMiddleY) {
            //     return
            // }

            // if (hoverStatusId !== statusId) {
            //     console.log("same col");
            //     return;
            // }
            // TODO: check drop, NOT hover

            moveItem(dragIndex, hoverIndex, statusTitle);
            // draggedtask.index = hoverIndex;
            console.log("HAPPEN");
        },
    });*/

    const [{ isDragging }, drag] = useDrag({
        type: "task",
        item: { type: "task", ...task, index },
        collect: monitor => ({
            isDragging: monitor.isDragging()
        })
    });

    const [taskClicked, setTaskClicked] = useState(false);

    const toggleModal = () => {
        setTaskClicked((prevState) => (!prevState))
    }

    const deleteTaskHandler = (e) => {
        e.stopPropagation();
        deleteTask(task.id);
        console.log("DELETE");
    }

    drag((ref));

    return (
        <div ref={ref}>
            <div

                className={`${isDragging ? classes.dashed : ""}`}
                onClick={toggleModal}
            >
                <div className={classes.task} >
                    <i className={`fa-solid fa-trash-can ${classes.taskTrash}`} onClick={deleteTaskHandler}></i>
                    <div className={classes.taskStatusWrapper}>
                        <div className={classes.taskStatus}>{task.icon}</div>
                    </div>
                    <p className={classes.taskTitle}>{task.title}</p>
                </div>
            </div>
            {taskClicked &&
                ReactDOM.createPortal(
                    <Modal
                        icon={task.icon}
                        title={task.title}
                        content={task.content}
                        onClose={toggleModal}
                    />, document.getElementById('overlay-root')
                )
            }
        </div>
    );
};

export default Task;
