import React, { useState, useRef, useCallback } from 'react';
import Picture from './Note';
import { useDrop } from 'react-dnd';
import { notesList } from '../data';



function DragDrop() {

    const [board, setBoard] = useState(notesList); // declare the final destination/notes here
    const moveImage = (dragIndex, hoverIndex) => {
        setBoard((prevBoard) => {
             const newItems = prevBoard.filter((_, idx) => idx !== dragIndex);
            newItems.splice(hoverIndex, 0, board[dragIndex]);
            console.log("moveImage: " + dragIndex, hoverIndex, newItems);
            return  [ ...newItems ];
        });
    };

    const addImageToBoard = (id) => {
        console.log("Dragging is over"); // printed when isOver is true
        const pictureList = notesList.filter((picture) => id === picture.id); // find the dragged image
        // notesList = notesList.filter((picture) => id !== picture.id);
        setBoard((board) => [...board, pictureList[0]]); // add to the board
    }

    return (
        <>
            {/* <div className='Pictures' > 
    {PicturesList.map((picture) => {
        return <Picture key={picture.id} id={picture.id} url={picture.url}/>;
    })} </div> */}
    {/* ref={drop} */}
            <div className='Board' style={{ border: '2px solid black', height: '100vh', display: 'flex', flexDirection: 'column'}}>
                {/*  onDrop={onDrop} */}
                {/* , color: isOver  */}
                {/* {console.log(board)} */}
                {board.map((picture, index) => {
                    return <Picture 
                    key={picture.id} 
                    id={picture.id} 
                    url={picture.url} 
                    index={index}
                    moveImage = {moveImage}
                    // ref={ref}
                    />;
                })}
            </div>
        </>
    )
}

export default DragDrop;
