import React, { useState, useRef, useCallback } from 'react';
import Note from './Note';
import { useDrop } from 'react-dnd';
import { notesList, columnsList } from '../data/panel-data';
import Column from './Column';
import classes from './Panel.module.css';




function DragDrop() {

    const [cards, setCards] = useState(notesList); // declare the final destination/notes here
    const [columns, setColumns] = useState(columnsList);

    const moveImage = (dragIndex, hoverIndex) => {
        setCards((prevcards) => {
            const newCards = prevcards.filter((_, idx) => idx !== dragIndex);
            newCards.splice(hoverIndex, 0, cards[dragIndex]);
            console.log(newCards);
            // console.log("moveImage: " + dragIndex, hoverIndex, newCards);
            return [...newCards];
        });
    };

    const onDrop = (item, columnName) => {
        const mapping = columns.find(col => col.columnName === columnName);
        setCards(prevCards => {
            // const newCard = prevCards.filter(i=> i.id === item.id)
            // newCard.columnName = columnName;
            // console.log("new carsd:" + newCard);
            // return [...prevCards, newCard];
            const newItems = prevCards
                .filter(i => i.id !== item.id)
                .concat({ ...item, columnName, icon: mapping.icon });
            // console.log("newItems " + newItems);
            return [...newItems];
        })
    }

    return (
        <>
            <div className="container">
                <div className={`${classes.cards}`}>
                    {/* , color: isOver  */}
                    {/* {console.log(cards)} */}
                    {columns.map((column) => {
                        return (
                            <div key={column.columnName} className={classes.columnWrapper}>
                                <h3 className='column-title'>{column.columnName.toUpperCase()}</h3>
                                <Column columnName={column.columnName} onDrop={onDrop}>
                                    {/* {console.log(cards)} */}
                                    {cards
                                        .filter((item, index) => column.columnName === item.columnName)
                                        .map((item, index) => {
                                            return <Note
                                                key={item.id}
                                                item={item}
                                                index={index}
                                                moveImage={moveImage}
                                            />;
                                        })
                                    }
                                </Column>
                            </div>
                        )
                    })}
                    {/* {cards.map((picture, index) => {
                    return <Note
                        key={picture.id}
                        id={picture.id}
                        url={picture.url}
                        index={index}
                        moveImage={moveImage}
                    // ref={ref}
                    />;
                })} */}
                </div>
            </div>
        </>
    )
}

export default DragDrop;
