                                <Dropdown.Item key={idx} value={color} className={`color-box ${(activeColor.code === color.code) ? "active" : ""} `} style={{ backgroundColor: `${color.code}` }} onClick={() => changeBgColorHandler(color)}>{color.name}</Dropdown.Item>

