import React, {useRef} from 'react';

import SinglePicture from "./SinglePicture";
import { useDrag, useDrop } from 'react-dnd';


function Picture({ id, url, index, moveImage }) {
    const ref = useRef(null);

    const [{ isDragging }, drag] = useDrag(() => ({
        type: "image", // tpe of draggable item
		accept: "card",
        item: {id: id}, // pass parametres accessible from board
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
        })
    }))
    const [{ handlerId }, drop] = useDrop({
		collect(monitor) {
			return {
				handlerId: monitor.getHandlerId(),
			}
		},
		hover(item, monitor) { /// hovered item underneath
			if (!ref.current) {
				return
			}
			const dragIndex = item.index
			const hoverIndex = index

			// Don't replace items with themselves
			if (dragIndex === hoverIndex) {
				return
			}

			// Determine rectangle on screen
			const hoverBoundingRect = ref.current?.getBoundingClientRect()

			// Get vertical middle
			const hoverMiddleY =
				(hoverBoundingRect.bottom - hoverBoundingRect.top) / 2

			// Determine mouse position
			const clientOffset = monitor.getClientOffset()

			// Get pixels to the top
			const hoverClientY = clientOffset.y - hoverBoundingRect.top

			// Only perform the move when the mouse has crossed half of the items height
			// When dragging downwards, only move when the cursor is below 50%
			// When dragging upwards, only move when the cursor is above 50%

			// Dragging downwards
			if (dragIndex < hoverIndex && hoverClientY < hoverMiddleY) {
				return
			}

			// Dragging upwards
			if (dragIndex > hoverIndex && hoverClientY > hoverMiddleY) {
				return
			}

			// Time to actually perform the action
			moveImage(dragIndex, hoverIndex)

			// Note: we're mutating the monitor item here!
			// Generally it's better to avoid mutations,
			// but it's good here for the sake of performance
			// to avoid expensive index searches.
			item.index = hoverIndex
		},
	})

    // <SinglePicture drag={drag} url={url} isDragging={isDragging}/>
    drag(drop(ref))
    return (
		<img
			ref={drag} // declare draggable item in order to track its position
			src={url} height="100px" alt="here"
			data-handler-id={handlerId}
			style={{ border: isDragging ? "15px solid pink" : "0" }}/>

	)
}

export default Picture
