import React, { useState } from "react";
import classes from "./BackgroundColor.module.css"

const colors = ["#028090", "#fffff", "purple", "#101010", "darkmagenta"];

const BackgroundColor = () => {
    // const [bgColor, setBgColor] = useState(colors);
    const [activeColor, setActiveColor] = useState(colors[0]);

    const changeBgColorHandler = (color) => {
        if (color !== activeColor) {
            setActiveColor(color);
        }
    }
    return (
        <>
            <style>{`body { background-color: ${activeColor}; }`}</style>
            {/* window.getComputedStyle( document.body ).backgroundColor */}
            <select name="selectedBg" defaultValue={`${activeColor}`}>
                {
                colors.map((color, idx) => {
                    // {console.log(idx)}
                return (
                    <option key={idx} value={color} className={`${classes.colorBox} ${(activeColor === color) ? classes.active : ""} `} style={{backgroundColor: `${color}`}} onClick={changeBgColorHandler(color)}></option>
                )
                })}
                {/* <option value="apple">Apple</option>
                <option value="banana">Banana</option>
                <option value="orange">Orange</option> */}
            </select>
            {/* <div className={classes.usedColor}></div>
            <div className={classes.dropdown}> */}

            {/* </div> */}
        </>
    );
};

export default BackgroundColor;
