import React, { useState } from "react";
import classes from "./BackgroundColor.module.css"
import Dropdown from 'react-bootstrap/Dropdown';

// Bootstrap CSS
import "bootstrap/dist/css/bootstrap.min.css";
// Bootstrap Bundle JS
import "bootstrap/dist/js/bootstrap.bundle.min";

const colors = ["#028090", "#fffff", "purple", "#101010", "darkmagenta"];

const BackgroundColor = () => {
    // const [bgColor, setBgColor] = useState(colors);
    const [activeColor, setActiveColor] = useState(colors[0]);

    const changeBgColorHandler = (color) => {
        // if (color !== activeColor) {
        setActiveColor(color);
        // }
    }

    const [theme, setTheme] = useState(null);
    const [dropdownOpen, setdropdownOpen] = useState(false);
    const resetTheme = () => {
        setTheme(null);
    };

    return (
        <>

            <style>{`body { background-color: ${activeColor}; }`}</style>
            {/* window.getComputedStyle( document.body ).backgroundColor */}
            <select name="selectedBg" defaultValue={`${activeColor}`}>
                {
                    colors.map((color, idx) => {
                        // {console.log(idx)}
                        return (
                            <option key={idx} value={color} className={`${classes.colorBox} ${(activeColor === color) ? classes.active : ""} `} style={{ backgroundColor: `${color}` }} onClick={() => changeBgColorHandler(color)}>L</option>
                        )
                    })}
                {/* <option value="apple">Apple</option>
                <option value="banana">Banana</option>
                <option value="orange">Orange</option> */}
            </select>
            <Dropdown>
                <Dropdown.Toggle variant="success" id="dropdown-basic">
                    Dropdown Button
                </Dropdown.Toggle>

                <Dropdown.Menu>
                    <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                    <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                    <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>
            {/* <div className={classes.usedColor}></div>
            <div className={classes.dropdown}> */}

            {/* </div> */}
        </>
    );
};

export default BackgroundColor;
